   ```php
   // routes/web.php
   Route::get('/conversions/{category}/{tool}', [ConversionController::class, 'show'])
       ->name('conversions.show')
       ->where(['category' => '[a-z-]+', 'tool' => '[a-z-]+']);

   // ConversionController.php
   public function show($category, $tool)
   {
       try {
           $toolData = $this->toolService->findBySlug($category, $tool);

           if (!$toolData) {
               abort(404, 'Tool not found');
           }

           return view('tools.show', [
               'tool' => $toolData,
               'category' => $category,
               'relatedTools' => $this->toolService->getRelated($tool)
           ]);
       } catch (\Exception $e) {
           Log::error('Tool page error', [
               'category' => $category,
               'tool' => $tool,
               'error' => $e->getMessage()
           ]);
           abort(500, 'Unable to load tool');
       }
   }
   ```

